import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import axios from 'axios';

const DetailsPage = () => {
  const [store, setStore] = useState()

  const { id } = useParams()


  useEffect(() => {
    axios.get(`http://localhost:8000/api/stores/${id}`)
      .then(response => setProducts(response.data))
      .catch(err => console.log(err))
  },[])
  return (
    <div>
      {
        store?
        <div class="card">
        {/* <img src={products.imageUrl} class="card-img-top" alt="card-img-top"/> */}
          <div class="card-body">
            <h5 class="card-title">{store.name}</h5>
            <p class="card-text">Store Number: {store.number}</p>
            <p class="card-text">Open?: {store.open}</p>
            <Link to={`/stores/${id}/edit`}>Edit</Link>
          </div>
      </div>:
      <h1>Loading...</h1>
      }

    </div>
  )
}

export default DetailsPage
